const encrypt = (str, key) => {
    let result = "";
    for (let i = 0; i < str.length; i++) {
        const encryptedChar = String.fromCharCode(str.charCodeAt(i) ^ key.charCodeAt(i % key.length));
        result += window.btoa(encryptedChar); // Codificar en Base64
    }
    return result;
};

const decrypt = (str, key) => {
    let result = "";
    for (let i = 0; i < str.length; i += 4) {
        const base64Chunk = str.slice(i, i + 4);
        const encryptedChar = window.atob(base64Chunk); // Decodificar desde Base64
        result += String.fromCharCode(encryptedChar.charCodeAt(0) ^ key.charCodeAt((i / 4) % key.length));
    }
    return result;
};

module.exports = {
    encrypt,
    decrypt,
};
