import React, { useState, useEffect } from "react";
import AuthService from "../../services/auth.service";
import GestionService from "../../services/gestion.service";
import EventBus from "../../common/EventBus";
import "../../styles/UserBoards/Ranking.css";

const Ranking = () => {
    const [students, setStudents] = useState([]);
    const [teacher, setTeacher] = useState([]);
    const [user, setUser] = useState(null);
    const [showRanking, setShowRanking] = useState(true)

    useEffect(() => {
        fetchStudentsWithSameTeacher();

        AuthService.getCurrentUserFromDB()
        .then((response) => {
          setUser(response.data);
        })
        .catch((error) => {
          if (error.response && error.response.status === 401) {
            EventBus.dispatch("logout");
          }
        });

        AuthService.getMyTeacher()
        .then((response) => {
          setTeacher(response.data);
          setShowRanking(response.data[0].flag_ranking)
        })
        .catch((error) => {
          if (error.response && error.response.status === 401) {
            EventBus.dispatch("logout");
          }
        });
    }, []);

    const fetchStudentsWithSameTeacher = () => {
        AuthService.getStudentsWithSameTeacher()
            .then((response) => {
                setStudents(response.data);
            })
            .catch((error) => {
                console.log(error);
            });
    };

    const handleShowRanking = (newVisibility) => {
        const teachers_out = teacher.map((teach) => {
            return { ...teach, flag_ranking: newVisibility };
        });
        setTeacher(teachers_out);
        console.log("El flag una vez entra en el handleShowRanking es " + teacher[0].flag_ranking)
        GestionService.updateRankingVisibility(teachers_out[0])
          .then(() => {
            setShowRanking(newVisibility)
          })
          .catch((error) =>
            console.error("Error actualizando la visibilidad del ranking:", error)
          );
    };

    return (
        <div className="container text-center">
            <h3 className="text-center mt-4">Ranking de alumnos</h3>
            {user?.username===user?.teacher && (
                <>
                    {teacher.map((teach) => (
                    <div className="gray-box">
                        <input
                            type="checkbox"
                            checked={teach.flag_ranking}
                            onChange={(e) =>{
                                handleShowRanking(e.target.checked)
                                console.log("Se ha enviado como" + e.target.checked)
                                }
                            }
                        />
                        <span className="ml-2">Ocultar Ranking a los alumnos</span>
                    </div>))}
                </>
            )}

            {showRanking && user?.username!==teacher?.username && (
            <table className="table mt-2">
                <thead>
                    <tr>
                        <th>Posición</th>
                        <th>Usuario</th>
                        <th>Puntuación</th>
                    </tr>
                </thead>
                <tbody>
                    {students.map((student, index) => (
                        <tr
                            key={index}
                            className={
                                index === 0
                                    ? "gold"
                                    : index === 1
                                    ? "silver"
                                    : index === 2
                                    ? "bronze"
                                    : ""
                            }
                        >
                            <td>{index + 1}</td>
                            <td>
                                {index === 0 && <>🥇 {student.username}</>}
                                {index === 1 && <>🥈 {student.username}</>}
                                {index === 2 && <>🥉 {student.username}</>}
                                {index >= 3 && student.username}
                            </td>
                            <td>{student.scores.reduce((acc, score) => acc + score, 0).toString().replace('.',',')}</td>
                        </tr>
                    ))}
                </tbody>
            </table>)}

            {user?.username===teacher?.username && (
            <table className="table mt-2">
                <thead>
                    <tr>
                        <th>Posición</th>
                        <th>Usuario</th>
                        <th>Puntuación</th>
                    </tr>
                </thead>
                <tbody>
                    {students.map((student, index) => (
                        <tr
                            key={index}
                            className={
                                index === 0
                                    ? "gold"
                                    : index === 1
                                    ? "silver"
                                    : index === 2
                                    ? "bronze"
                                    : ""
                            }
                        >
                            <td>{index + 1}</td>
                            <td>
                                {index === 0 && <>🥇 {student.username}</>}
                                {index === 1 && <>🥈 {student.username}</>}
                                {index === 2 && <>🥉 {student.username}</>}
                                {index >= 3 && student.username}
                            </td>
                            <td>{student.scores.reduce((acc, score) => acc + score, 0).toString().replace('.',',')}</td>
                        </tr>
                    ))}
                </tbody>
            </table>)}
        </div>
    );
};

export default Ranking;
