import React, { useState, useEffect } from "react";
import AuthService from "../../services/auth.service";
import "../../styles/UserBoards/BoardAdmin.css";
import EventBus from "../../common/EventBus";

const BoardAdmin = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [content, setContent] = useState();
  const [user, setUser] = useState(null);
  const [newUser, setNewUser] = useState({
    fullName: "",
    domain: "",
    email: "",
    password: "",
    role: "",
    isFormValid: false
  });

  useEffect(() => {
    AuthService.getCurrentUserFromDB()
      .then((response) => {
        setUser(response.data);
      })
      .catch((error) => {
        const _content =
          (error.response &&
            error.response.data &&
            error.response.data.message) ||
          error.message ||
          error.toString();

        setContent(_content);

        if (error.response && error.response.status === 401) {
          EventBus.dispatch("logout");
        }
      });

    getAllUsersExceptAdmins();
  }, []);

  const getAllUsersExceptAdmins = () => {
    AuthService.getAllUsersExceptAdmins()
      .then((response) => {
        setUsers(response.data);
        console.log("El frontend ha recibido", response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const handleSearchTermChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleNewUserChange = (e) => {
    const { name, value } = e.target;

    setNewUser((prevUser) => ({
      ...prevUser,
      [name]: value,
      isFormValid: validateForm({ ...prevUser, [name]: value })
    }));
  };

  const validateForm = (user) => {
    const { fullName, domain, email, password, role } = user;

    const isFullNameValid = fullName.trim() !== "";
    const isDomainValid = domain.trim() !== "" && (domain.endsWith(".com") || domain.endsWith(".es"));
    const isEmailValid = email.trim() !== "";
    const isPasswordValid = password.trim() !== "";
    const isRoleValid = role.trim() !== "";

    return isFullNameValid && isDomainValid && isEmailValid && isPasswordValid && isRoleValid;
  };

  const handleDomainChange = (e) => {
    setNewUser({
      ...newUser,
      domain: e.target.value
    });
  };

  const generateUsername = (fullName) => {
    // Lógica para generar el nombre de usuario
    const names = fullName.split(" ");
    const firstName = names[0].toLowerCase();
    const lastName = names[names.length - 1].toLowerCase();
    const username = firstName.substring(0, 3) + lastName.substring(0, 3) + (Math.floor(Math.random() * (90 - 10 + 1)) + 10).toString();
    return username;
  };

  const generateEmail = (username, domain) => {
    if (domain.endsWith(".es") || domain.endsWith(".com")) {
      return username + "@" + domain;
    } else {
      // Mostrar un mensaje de error o manejarlo según tus necesidades
      console.log("Dominio inválido");
      return "";
    }
  };


  const generatePassword = () => {
    // Lógica para generar una contraseña aleatoria
    const characters = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
    let password = "";
    for (let i = 0; i < 8; i++) {
      password += characters.charAt(Math.floor(Math.random() * characters.length));
    }
    return password;
  };

  const createUser = () => {
    const username = generateUsername(newUser.fullName);
    const email = generateEmail(username, newUser.domain);
    const password = generatePassword();

    AuthService.register(email, password, newUser.teacher, newUser.role)
      .then((response) => {
        // Mostrar la contraseña generada
        alert("Contraseña generada: " + password + ". Por favor, cópiela para mandársela al usuario.");
        // Actualizar la lista de usuarios después de crear uno
        getAllUsersExceptAdmins();
        // Restablecer los campos del nuevo usuario
        setNewUser({
          fullName: "",
          domain: "",
          email: "",
          password: "",
          role: "",
          isFormValid: false
        });
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const filterUsers = () => {
    console.log("El frontal recibe así a los usuarios", users);
    return users.filter((results) =>
      results.email.includes(searchTerm.toLowerCase())
    );
  };

  const deleteUser = (user) => {
    let confirmMessage =
      "¿Estás seguro de borrar al usuario? Esta acción no se puede deshacer.";

    if (user.roles[0].name.includes("teacher")) {
      confirmMessage +=
        " Esta acción también eliminará a sus alumnos asociados.";
    }

    const confirmDelete = window.confirm(confirmMessage);

    if (confirmDelete) {
      AuthService.deleteAccountByEmail(user.email)
        .then(() => {
          // Actualizar la lista de usuarios después de borrar uno
          getAllUsersExceptAdmins();
        })
        .catch((error) => {
          console.log(error);
        });
    }
  };

  return (
    <>
      {user ? (
        <div className="board-admin">
          <h3 className="text-center mt-4">Panel de administración</h3>
          <input
            type="text"
            className="form-control mb-3"
            placeholder="Buscar usuarios..."
            value={searchTerm}
            onChange={handleSearchTermChange}
          />
          <ul className="list-group">
            {filterUsers().map((user) => (
              <li key={user.id} className="list-group-item">
                <div className="user-details">
                  <div className="user-email">{user.email}</div>
                  <div className="user-role">
                    {
                      user.roles[0].name === "teacher" ?
                        "Profesor" :
                        "Alumno"
                    }
                  </div>
                </div>
                <div className="delete-button">
                  <button
                    type="button"
                    className="btn btn-danger"
                    onClick={() => deleteUser(user)}
                  >
                    Eliminar
                  </button>
                </div>
              </li>
            ))}
          </ul>
          <div className="generate-credentials">
            <h4>Generar credenciales</h4>
            <div className="form-group">
              <label>Nombre y apellidos:</label>
              <input
                type="text"
                className="form-control"
                name="fullName"
                value={newUser.fullName}
                onChange={handleNewUserChange}
              />
              {!newUser.isFormValid && newUser.fullName.trim() === "" && (
                <div className="error-message">Por favor, introduce un nombre y apellidos válidos.</div>
              )}
            </div>
            <div className="form-group">
              <label>Dominio (.com o .es):</label>
              <input
                type="text"
                className="form-control"
                name="domain"
                value={newUser.domain}
                onChange={handleDomainChange}
              />
              {!newUser.isFormValid && newUser.domain.trim() === "" && (
                <div className="error-message">Por favor, introduce un dominio válido (.com o .es).</div>
              )}
            </div>
            <div className="form-group">
              <label>Nombre de usuario generado:</label>
              <input
                type="text"
                className="form-control"
                value={generateUsername(newUser.fullName) + (newUser.domain ? newUser.domain : "")}
                readOnly
              />
            </div>
            <div className="form-group">
              <label>Rol:</label>
              <select
                className="form-control"
                name="role"
                value={newUser.role}
                onChange={handleNewUserChange}
              >
                <option value="">Seleccione un rol</option>
                <option value="student">Alumno</option>
                <option value="teacher">Profesor</option>
              </select>
              {!newUser.isFormValid && newUser.role.trim() === "" && (
                <div className="error-message">Por favor, seleccione un rol válido.</div>
              )}
            </div>
            <button
              type="button"
              className="btn btn-primary"
              onClick={createUser}
              disabled={!newUser.isFormValid}
            >
              Crear usuario
            </button>
          </div>
        </div>
      ) : (
        <h3>{content}</h3>
      )}
    </>
  );
};

export default BoardAdmin;
