import React, { useState, useEffect } from "react";

import UserService from "../../services/user.service";
import EventBus from "../../common/EventBus";
import Email from "./Email";
import Roles from "./Roles";
import "../../styles/Profile/Profile.css";
import AuthService from "../../services/auth.service";

const Profile = () => {
  const [content, setContent] = useState();
  const [showMessage, setShowMessage] = useState(false);
  const currentUser = AuthService.getCurrentUser();

  useEffect(() => {
    UserService.getUserBoard().then(
      (response) => {
        setContent("Logged");
      },
      (error) => {
        const _content =
          (error.response &&
            error.response.data &&
            error.response.data.message) ||
          error.message ||
          error.toString();

        setContent(_content);

        if (error.response && error.response.status === 401) {
          EventBus.dispatch("logout");
        }
      }
    );
  }, []);

  const handleEraseStats = () => {
    const confirmed = window.confirm("¿Estás seguro? Se borrarán los ejercicios resueltos, puntuaciones, etc.");
    if (confirmed){
      AuthService.eraseStats()
        .then((response) => {
          setShowMessage(true);
          setTimeout(() => {
            setShowMessage(false);
          }, 3000); // Ocultar el mensaje después de 3 segundos
        })
        .catch((error) => console.error("Error borrando las estadísticas:", error));
    }
  };

  return (
    <div className="container">
      <section className="profile-section">
        <h2>Datos del perfil</h2>
        <header className="jumbotron">
          {content === "Logged" ? (
            <>
              <Email email={currentUser.email} />
              <Roles roles={currentUser.roles} />
            </>
          ) : (
            <>{content}</>
          )}
        </header>
      </section>

      <section className="config-section">
        <h2>Configuración</h2>
        {content === "Logged" ? (
          <>
            <button
              className="btn btn-custom btn-block"
              onClick={handleEraseStats}
            >
              Borrar Estadísticas
            </button>
            {showMessage && (
              <div className="input-feedback alert alert-success text-center">
                ¡Las estadísticas han sido borradas!
              </div>
            )}
          </>
        ) : (
          <>{content}</>
        )}
      </section>
    </div>
  );
};

export default Profile;
