import React from "react";

import '../../styles/Exercises/DropdownMenu.css';

import block1Image from "../../assets/block1.png";
import block2Image from "../../assets/block2.png";
import block3Image from "../../assets/block3.png";
import block4Image from "../../assets/block4.png";

const BlockButtons = ({ handleSelect }) => {
  return (
    <div className="text-center">
      <div className="d-flex justify-content-between">
        <button
          className="btn btn-bloque btn-lg"
          onClick={() => handleSelect(1)}
        >
          <img src={block1Image} alt="Aproximaciones, ordenación, múltiplos y divisores" />
          <span>Bloque I - Números y Operaciones</span>
        </button>
        <button
          className="btn btn-bloque btn-lg"
          onClick={() => handleSelect(2)}
        >
          <img src={block2Image} alt="Ángulos, monedas, horarios y otras medidas" />
          <span>Bloque II - La Medida</span>
        </button>
      </div>
      <div className="d-flex justify-content-between mt-3">
        <button
          className="btn btn-bloque btn-lg"
          onClick={() => handleSelect(3)}
        >
          <img src={block3Image} alt="Tipos de triángulos según sus ángulos, áreas y perímetros" />
          <span>Bloque III - Formas geométricas y Orientación Espacial</span>
        </button>
        <button
          className="btn btn-bloque btn-lg"
          onClick={() => handleSelect(4)}
        >
          <img src={block4Image} alt="Medias, modas, medianas, máximos, mínimos y gráficas" />
          <span>Bloque IV - Organización de la Información</span>
        </button>
      </div>
    </div>
  );
};

export default BlockButtons;
