import React, { useState, useEffect } from "react";
import AuthService from "../../services/auth.service";
import GestionService from "../../services/gestion.service";
import EventBus from "../../common/EventBus";
import "../../styles/UserBoards/Ranking.css";

const Ranking = () => {
    const [students, setStudents] = useState([]);
    const [teacher, setTeacher] = useState();
    const [user, setUser] = useState(null);
    const [showRanking, setShowRanking] = useState(false)

    useEffect(() => {
        fetchStudentsWithSameTeacher();

        AuthService.getCurrentUserFromDB()
        .then((response) => {
          setUser(response.data);
        })
        .catch((error) => {
          if (error.response && error.response.status === 401) {
            EventBus.dispatch("logout");
          }
        });

        myTeacher();

    }, []);

    const fetchStudentsWithSameTeacher = () => {
        AuthService.getStudentsWithSameTeacher()
            .then((response) => {
                setStudents(response.data);
            })
            .catch((error) => {
                console.log(error);
            });
    };

    const myTeacher = () => {
        AuthService.getMyTeacher()
        .then((response) => {
          setTeacher(response.data);
          setShowRanking(response.data.flag_ranking)
          console.log("Se ejecuta el getMyTeacher y se recibe el flag a " + response.data.flag_ranking )
        })
        .catch((error) => {
          if (error.response && error.response.status === 401) {
            EventBus.dispatch("logout");
          }
        });
    }

    const handleShowRanking = (flag) => {
        const teacher_updated = { ...teacher, flag_ranking: flag }

        console.log("El flag una vez entra en el handleShowRanking es " + flag)
        GestionService.updateRankingVisibility(teacher_updated, flag)
          .then(() => {
            setShowRanking(flag)
            setTeacher(teacher_updated)
            console.log("Dentro del hableShowRanking se ha puesto el flag a " + flag+ ". Vuelvo a llamar al getMyTeachers")
          })
          .catch((error) =>
            console.error("Error actualizando la visibilidad del ranking:", error)
          );
          myTeacher();
    };

    return (
        <div className="container text-center">
            <h3 className="text-center mt-4">Ranking de alumnos</h3>
            {user?.username===user?.teacher && (
                <div className="gray-box">
                    <input
                        type="checkbox"
                        checked={teacher.flag_ranking}
                        onChange={(e) =>{
                            handleShowRanking(e.target.checked)
                            console.log("Una vez sale del handle, si consultamos a teacher el flag es " + teacher[0].flag_ranking)
                            console.log("Una vez sale del handle, y si consultamos a showRanking es  " + showRanking)
                            }
                        }
                    />
                    <span className="ml-2">Ocultar Ranking a los alumnos</span>
                </div>
            )}

            {showRanking && user?.username!==teacher?.username && (
            <table className="table mt-2">
                <thead>
                    <tr>
                        <th>Posici贸n</th>
                        <th>Usuario</th>
                        <th>Puntuaci贸n</th>
                    </tr>
                </thead>
                <tbody>
                    {students.map((student, index) => (
                        <tr
                            key={index}
                            className={
                                index === 0
                                    ? "gold"
                                    : index === 1
                                    ? "silver"
                                    : index === 2
                                    ? "bronze"
                                    : ""
                            }
                        >
                            <td>{index + 1}</td>
                            <td>
                                {index === 0 && <> {student.username}</>}
                                {index === 1 && <> {student.username}</>}
                                {index === 2 && <> {student.username}</>}
                                {index >= 3 && student.username}
                            </td>
                            <td>{student.scores.reduce((acc, score) => acc + score, 0).toString().replace('.',',')}</td>
                        </tr>
                    ))}
                </tbody>
            </table>)}

            {user?.username===teacher?.username && (
            <table className="table mt-2">
                <thead>
                    <tr>
                        <th>Posici贸n</th>
                        <th>Usuario</th>
                        <th>Puntuaci贸n</th>
                    </tr>
                </thead>
                <tbody>
                    {students.map((student, index) => (
                        <tr
                            key={index}
                            className={
                                index === 0
                                    ? "gold"
                                    : index === 1
                                    ? "silver"
                                    : index === 2
                                    ? "bronze"
                                    : ""
                            }
                        >
                            <td>{index + 1}</td>
                            <td>
                                {index === 0 && <> {student.username}</>}
                                {index === 1 && <> {student.username}</>}
                                {index === 2 && <> {student.username}</>}
                                {index >= 3 && student.username}
                            </td>
                            <td>{student.scores.reduce((acc, score) => acc + score, 0).toString().replace('.',',')}</td>
                        </tr>
                    ))}
                </tbody>
            </table>)}
        </div>
    );
};

export default Ranking;
