import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCheckSquare, faSquare } from "@fortawesome/free-regular-svg-icons";

const ExerciseBlock = ({ blockNumber, exercises, onExerciseVisibilityChange }) => {
  return (
    <div className="col">
      <div className="card">
        <div className="card-body">
          <h5 className="card-title text-center">Bloque {blockNumber}</h5>
          {exercises.map((exercise) => (
            <div key={exercise._id} className="form-check">
              <input
                className="form-check-input"
                type="checkbox"
                checked={exercise.flag_active}
                onChange={(e) => onExerciseVisibilityChange(exercise._id, e.target.checked)}
              />
              <label className="form-check-label" htmlFor={`exercise-${exercise._id}`}>
                {exercise.name}
              </label>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ExerciseBlock;
