import React, { useState, useEffect } from "react";
import AuthService from "../services/auth.service";

const BoardAdmin = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [newUser, setNewUser] = useState({
    fullName: "",
    email: "",
    password: "",
    role: "",
    domain: "@gmail.com"
  });

  useEffect(() => {
    getAllUsersExceptAdmins();
  }, []);

  const getAllUsersExceptAdmins = () => {
    AuthService.getAllUsersExceptAdmin()
      .then((response) => {
        setUsers(response.data.users);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const handleSearchTermChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleNewUserChange = (e) => {
    setNewUser({
      ...newUser,
      [e.target.name]: e.target.value
    });
  };

  const generateUsername = (fullName) => {
    const names = fullName.split(" ");
    const firstName = names[0].toLowerCase();
    const lastName = names[names.length - 1].toLowerCase();
    const username = firstName.substring(0, 3) + lastName.substring(0, 3);
    return username;
  };

  const generateEmail = (username, domain) => {
    return username + domain;
  };

  const generatePassword = () => {
    const characters = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
    let password = "";
    for (let i = 0; i < 8; i++) {
      password += characters.charAt(Math.floor(Math.random() * characters.length));
    }
    return password;
  };

  const createUser = () => {
    const username = generateUsername(newUser.fullName);
    const email = generateEmail(username, newUser.domain);
    const password = generatePassword();

    AuthService.register(email, password, newUser.role)
      .then((response) => {
        alert("Contraseña generada: " + password);
        getAllUsersExceptAdmins();
        setNewUser({
          fullName: "",
          email: "",
          password: "",
          role: "",
          domain: "@gmail.com"
        });
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <div className="board-admin">
      <h3>Panel de Administración</h3>
      <input
        type="text"
        className="form-control mb-3"
        placeholder="Buscar usuarios..."
        value={searchTerm}
        onChange={handleSearchTermChange}
      />
      <ul className="list-group">
        {users
          .filter((user) => {
            if (searchTerm === "") {
              return user;
            } else if (
              user.fullName.toLowerCase().includes(searchTerm.toLowerCase())
            ) {
              return user;
            }
          })
          .map((user) => (
            <li className="list-group-item" key={user._id}>
              {user.fullName} ({user.email})
            </li>
          ))}
      </ul>
      <div className="generate-credentials">
        <h4>Generar credenciales</h4>
        <div className="form-group">
          <label>Nombre y apellidos:</label>
          <input
            type="text"
            className="form-control"
            name="fullName"
            value={newUser.fullName}
            onChange={handleNewUserChange}
          />
        </div>
        <div className="form-group">
          <label>Rol:</label>
          <select
            className="form-control"
            name="role"
            value={newUser.role}
            onChange={handleNewUserChange}
          >
            <option value="student">Alumno</option>
            <option value="teacher">Profesor</option>
          </select>
        </div>
        <button
          type="button"
          className="btn btn-primary"
          onClick={createUser}
        >
          Crear usuario
        </button>
      </div>
    </div>
  );
};

export default BoardAdmin;
