import React, { useState, useEffect } from "react";

import AuthService from "../../services/auth.service";

const Ranking = () => {

    const [students, setStudents] = useState([]);

    useEffect(() => {
        fetchStudentsWithSameTeacher();
      }, []);
    
    const fetchStudentsWithSameTeacher = () => {
        AuthService.getStudentsWithSameTeacher()
        .then((response) => {
            setStudents(response.data);
        })
        .catch((error) => {
            console.log(error);
        });
    };

    return (
        <div className="container">
            <h2>Ranking de Usuarios</h2>
            <table className="table table-striped">
                <thead>
                    <tr>
                        <th>PosiciÃ³n</th>
                        <th>Usuario</th>
                        <th>PuntuaciÃ³n</th>
                    </tr>
                </thead>
                <tbody>
                    {students.map((student, index) => (
                        <tr key={index}>
                            <td>
                                {index === 0 && <span className="gold">ðŸ¥‡</span>}
                                {index === 1 && <span className="silver">ðŸ¥ˆ</span>}
                                {index === 2 && <span className="bronze">ðŸ¥‰</span>}
                                {index >= 3 && index + 1}
                            </td>
                            <td>{student.username}</td>
                            <td>{student.scores.reduce((acc, score) => acc + score, 0)}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Ranking;
