import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import logo from "../matemapi-logo.png"; // Reemplaza con la ruta de tu logo

import UserService from "../services/user.service";
import EventBus from "../common/EventBus";

const Home = () => {
  const [content, setContent] = useState("");

  useEffect(() => {
    UserService.getPublicContent().then(
      (response) => {
        setContent(response.data);
      },
      (error) => {
        const _content =
          (error.response &&
            error.response.data &&
            error.response.data.message) ||
          error.message ||
          error.toString();

        setContent(_content);

        if (error.response && error.response.status === 401) {
          EventBus.dispatch("logout");
        }
      }
    );
  }, []);

  return (
    <div className="container">
      <header className="jumbotron">
        <div className="container text-center">
          <img src={logo} alt="Logo" className="mb-4" width="200" /> {/* Ajusta el ancho de acuerdo a tus necesidades */}
          <h2 className="display-4">Bienvenido a MatemAPI</h2>
          <p className="lead">MatemAPI es una aplicación web para el correcto aprendizaje de ejercicios de matemáticas de nivel escolar (tercer ciclo).</p>
          <p>
            <Link to={"/resolver"} className="btn btn-primary btn-lg">Comenzar &raquo;</Link>
          </p>
        </div>
      </header>
    </div>
  );
};

export default Home;
